SELECT no, boardNo, userNo, nickname, content, regdate
FROM comments
WHERE no=#{no}

SELECT count(*)
FROM comments


		AND boardNo=#{boardNo}


		AND userNo=#{userNo}

SELECT no, boardNo, userNo, nickname, content, regdate
FROM comments


		AND boardNo=#{boardNo}


		AND userNo=#{userNo}


ORDER BY regdate ASC
LIMIT #{page.start}, #{page.end}
